SELECT BOARD.ROOM_IDX, BOARD.MEM_EMAIL, BOARD.SUBJECT, BOARD.DO_EN, BOARD.GU_GUN_EUP_ENG, BOARD.ADDRESS,BOARD.ADDRESS_DETAIL, BOARD.ROOM_REPORTING_DATE, IMG.ROOM_IMG_NAME, IMG.ROOM_IMG_ORI_NAME, INFO.BUILD_TYPE, INFO.ROOM_TYPE, INFO.BULID_LAYERS, INFO.FLOOR_LAYERS, INFO.FLOOR_TYPE, INFO.STAY_NUM_MIN, INFO.DEPOSIT, INFO.MONTHLY_RENT, INFO.MANAGEMENT_EXPENSE, INFO.AVAILABLE_DATE, OPT.ROOM_IDX, OPT.CLOSET, OPT.DESK, OPT.CHAIR, OPT.BAD_TYPE, OPT.AIR_CONDITIONER, OPT.HEATING, OPT.KITCHEN_STOVE, OPT.REFRIGERATOR, OPT.LAUNDRY, OPT.PET, OPT.INTERNET, OPT.WIFI, OPT.ELEVATOR, OPT.SINK, OPT.BATHROOM, OPT.PARKING FROM T_ROOM_BOARD BOARD, T_ROOM_IMG IMG, T_ROOM_INFO INFO, T_ROOM_OPTION OPT WHERE BOARD.ROOM_IDX=IMG.ROOM_IDX AND BOARD.ROOM_IDX=INFO.ROOM_IDX AND BOARD.ROOM_IDX=OPT.ROOM_IDX AND BOARD.GU_GUN_EUP_ENG IN (
#{sigugun}
)
ORDER BY BOARD.ROOM_IDX
